import React, { useEffect } from "react";
import styled from "styled-components";
import { useAppSelector } from "../store/hooks";

const ButtonContainer = styled.div`
  display: flex;
  justify-content: center;
  border: none;
  background-color: transparent;
`;

const Btn = styled.button`
  padding: 8px 16px;
  background-color: #f38c29;
  color: #ffffff;
  font-weight: bold;
  border: none;
  cursor: pointer;
  padding: 15px;
  border-radius: 50px;
  margin: 10px;

  @media (min-width: 820px) {
    padding: 20px;
  }
`;

interface ButtonProps {
  showFavorites: boolean;
}

const Button: React.FC<ButtonProps> = ({ showFavorites }) => {
  const favorites = useAppSelector((state) => state.favorites);

  const handleShowAllVeggies = () => {
    window.history.replaceState(null, "", "/show-all-veggies");
  };

  const handleFavorites = () => {
    window.history.replaceState(null, "", "/favorites");
  };

  useEffect(() => {
    if (showFavorites) {
      handleFavorites();
    } else {
      handleShowAllVeggies();
    }
  }, [showFavorites]);

  return (
    <ButtonContainer>
      <Btn onClick={handleShowAllVeggies}>Show all the good Veggies</Btn>
      <Btn onClick={handleFavorites}>
        Only show my favorite veggies ({favorites.length})
      </Btn>
    </ButtonContainer>
  );
};

export default Button;
