import React, { useState } from "react";
import styled from "styled-components";
import logo from "../../assets/images/banner.svg";
import Button from "../Button";

const HeaderContainer = styled.div`
  background-color: #142610;
  background-size: auto 70%;
  background-image: url(${logo});
  background-repeat: no-repeat;
  background-position: center;
  width: 100%;

  @media (min-width: 1200px) {
    padding-top: 40px;
  }
`;

const ButtonContainer = styled.div`
  display: flex;
  justify-content: center;
  gap: 20px;
  transform: translateY(50%);
`;

const Header = () => {
  const [isShowAllActive, setIsShowAllActive] = useState(true); // State för att hålla reda på om "Show all the good Veggies"-knappen är aktiv
  const [isShowFavoritesActive, setIsShowFavoritesActive] = useState(false); // State för att hålla reda på om "Only show my favorite veggies"-knappen är aktiv

  const handleShowAllVeggies = () => {
    setIsShowAllActive(true);
    setIsShowFavoritesActive(false);
    // Uppdatera din kod för att visa alla kort oavsett favoritstatus
  };

  const handleShowFavorites = () => {
    setIsShowAllActive(false);
    setIsShowFavoritesActive(true);
    // Uppdatera din kod för att visa endast favoritkort
  };

  return (
    <HeaderContainer>
      <h1>My favorite veggies</h1>
      <ButtonContainer>
        <Button
          handleShowAllVeggies={handleShowAllVeggies}
          handleShowFavorites={handleShowFavorites}
          isShowAllActive={isShowAllActive}
          isShowFavoritesActive={isShowFavoritesActive}
        />
      </ButtonContainer>
    </HeaderContainer>
  );
};

export default Header;
