import React, { useState, useContext, useEffect } from "react";
import styled from "styled-components";
import { FavoritesContext } from "../../AppContext";

const ButtonContainer = styled.div`
  display: flex;
  justify-content: center;
  border: none;
  background-color: transparent;
`;

const Btn = styled.button`
  padding: 8px 16px;
  background-color: #f38c29;
  color: #ffffff;
  font-weight: bold;
  border: none;
  cursor: pointer;
  padding: 15px;
  border-radius: 50px;
  margin: 10px;

  @media (min-width: 820px) {
    padding: 20px;
  }
`;

const Button = () => {
  const { setFilterFavorites } = useContext(FavoritesContext);

  const [activeButton, setActiveButton] = useState<number>(0);

  const handleClick = (index: number) => {
    setActiveButton(index);
    setFilterFavorites(index === 1);
  };

  return (
    <ButtonContainer>
      <Btn
        style={{
          backgroundColor: activeButton === 0 ? "#f38c29" : "transparent",
          color: activeButton === 0 ? "#ffffff" : "#f38c29",
        }}
        onClick={() => handleClick(0)}
      >
        Show all the good Veggies
      </Btn>
      <Btn
        style={{
          backgroundColor: activeButton === 1 ? "#f38c29" : "transparent",
          color: activeButton === 1 ? "#ffffff" : "#f38c29",
        }}
        onClick={() => handleClick(1)}
      >
        Only show my favorite veggies
      </Btn>
    </ButtonContainer>
  );
};

export default Button;
