import React from "react";
import styled from "styled-components";

interface ButtonProps {
  handleShowAllVeggies: () => void;
  handleShowFavorites: () => void;
  isShowAllActive: boolean; // Lägg till en prop för att indikera om "Show all the good Veggies"-knappen är aktiv
  isShowFavoritesActive: boolean; // Lägg till en prop för att indikera om "Only show my favorite veggies"-knappen är aktiv
}

const ButtonContainer = styled.div`
  display: flex;
  justify-content: center;
  border: none;
  background-color: transparent;
`;

const Btn = styled.button`
  padding: 8px 16px;
  background-color: #f38c29;
  color: #ffffff;
  font-weight: bold;
  border: none;
  cursor: pointer;
  padding: 15px;
  border-radius: 50px;
  margin: 10px;

  @media (min-width: 820px) {
    padding: 20px;
  }
`;

/* const Button = () => {
  const handleShowAllVeggies = () => {
    window.location.href = "/show-all-veggies";
  };

  const handleFavorites = () => {
    window.location.href = "/favorites";
  }; */

const Button: React.FC<ButtonProps> = ({
  handleShowAllVeggies,
  handleShowFavorites,
  isShowAllActive,
  isShowFavoritesActive,
}) => {
  return (
    <ButtonContainer>
      <Btn
        onClick={handleShowAllVeggies}
        style={{
          backgroundColor: isShowAllActive ? "#f38c29" : "transparent",
          color: isShowAllActive ? "#ffffff" : "#f38c29",
        }}
      >
        Show all the good Veggies
      </Btn>
      <Btn
        onClick={handleShowFavorites}
        style={{
          backgroundColor: isShowFavoritesActive ? "#f38c29" : "transparent",
          color: isShowFavoritesActive ? "#ffffff" : "#f38c29",
        }}
      >
        Only show my favorite veggies
      </Btn>
    </ButtonContainer>
  );
};

export default Button;
