import styled from "styled-components";
import { device } from "./../MediaQueries/";

export const HeaderContainer = styled.header`
  padding: 4em;
  background: papayawhip;

  @media ${device.laptop} {
    height: 10rem;
    width: 100vw;
  }

  /*   h1 {
    margin-top: 0;
    font-family: Cherry;
    padding-top: 1rem;
    text-align: center;
    font-size: 3rem;
    @media ${device.laptop} {
      font-size: 4rem;
    }
  } */
`;
